insert into ssafytrip.notice (notice_id, title, content, created_at)
values  (1, 'Summer race 공지문', '<p>안녕하세요 회원님들.</p><p>다음주부터 있을 <span style="color: rgb(230, 0, 0);">Summer race</span> 관련하여 공지사항이 있습니다.</p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/3a42b8a9-edummy1.png"></p><p><br></p><p><br></p><p>최적의 알고리즘을 구성하여 최고의 속도로 우승에 도전해보세요~! </p><p><br></p><p><br></p>', '2024-05-23 11:12:27'),
        (2, '2025년 VIP 선정 기준 및 혜택 변경 사전 안내', '<p><span style="color: rgb(102, 102, 102);">안녕하세요 </span><strong style="color: rgb(102, 102, 102);">Cloud Trip</strong><span style="color: rgb(102, 102, 102);">입니다.</span></p><p><span style="color: rgb(102, 102, 102);">당사를 지속적으로 이용해 주신 VIP 회원님께 다시 한번 감사 말씀 드립니다.&nbsp;</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">2025년 1월 1일부로 VIP 선정 기준과 등급 혜택이 일부 상향 조정될 예정이므로 사전 안내해 드립니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">변경된 내용이 모두 충족되어 VIP 대상 유지 및 혜택을 제공해 드릴 수 있기를 기대하겠습니다.</span></p><p><span style="color: rgb(102, 102, 102);">갑진년, 푸른 용의 해에 큰 희망과 성취로 고객님 가정에 행운이 넘쳐나시길 바랍니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">자세한 내용은 하단 표를 확인해 주시기 바랍니다.</span></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/f58a90d2-adummy2.jpeg"></p>', '2024-05-23 11:15:26'),
        (3, '베스트 여행 후기 이벤트 안내', '<p><span style="color: rgb(102, 102, 102);">안녕하세요? </span><strong style="color: rgb(102, 102, 102);">Cloud Trip</strong><span style="color: rgb(102, 102, 102);">입니다.</span></p><p><span style="color: rgb(102, 102, 102);">잠시 중단 하였던 여행 후기 이벤트를 2022년 7월부터 시작하고자 합니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">고객님의 소중한 이야기 중&nbsp;‘베스트 여행 후기’를 선정하여</span></p><p><span style="color: rgb(102, 102, 102);">감사의 마음을 전하는 이벤트입니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/2bd5d4bc-5dummy3.jpeg"></p><p><br></p><p><span style="color: rgb(102, 102, 102);">베스트 후기는 매월 초&nbsp;발표할 예정이며,&nbsp;지역별 균형에 맞춰 선정할 수 있습니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><span style="color: rgb(102, 102, 102);">다시,&nbsp;함께 참좋은여행을 이용하고</span></p><p><span style="color: rgb(102, 102, 102);">여행의 감동을 공유해 주셔서 진심으로 감사드립니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><span style="color: rgb(102, 102, 102);">많은 관심 부탁드리며 다음 여행도 참좋은여행에서 만나 뵙길 기대하겠습니다.</span></p><p><span style="color: rgb(102, 102, 102);">감사합니다.</span></p><p><br></p>', '2024-05-23 11:16:33'),
        (4, 'CF 론칭 이벤트(가족 패키지여행 예약) 당첨자 발표', '<p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/081608a1-4dummy4.jpeg"></p><p><br></p><p><span style="color: rgb(102, 102, 102);">안녕하세요. </span><strong style="color: rgb(102, 102, 102);">Cloud Trip</strong><span style="color: rgb(102, 102, 102);">입니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">2023년 12월 1일부터 2024년 2월 29일까지 진행한</span></p><p><span style="color: rgb(102, 102, 102);">CF 론칭 기념, 가족 패키지여행 예약 이벤트</span></p><p><span style="color: rgb(102, 102, 102);">당첨을 진심으로 축하드립니다.</span></p><p><br></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/29262d35-5%E1%84%89%E1%85%B3%E1%84%8F%E1%85%B3%E1%84%85%E1%85%B5%E1%86%AB%E1%84%89%E1%85%A3%E1%86%BA%202024-05-23%20%E1%84%8B%E1%85%A9%E1%84%92%E1%85%AE%208.18.17.png"></p><p><br></p><p><br></p><p><span style="color: rgb(102, 102, 102);">☎ 이벤트 관련 문의</span></p><p><span style="color: rgb(102, 102, 102);">: 마케팅팀 02-1234-5678</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">☎ 크루즈 상품 관련 문의</span></p><p><span style="color: rgb(102, 102, 102);">: 법인/테마팀 02-9876-5432</span></p><p><br></p><p><br></p><p><strong style="color: rgb(211, 84, 0);">※ 유의사항</strong></p><p><span style="color: rgb(102, 102, 102);">- 경품 당첨자와 출발자는 가족관계여야 하며, 관련 서류를 요청할 수 있습니다.</span></p><p><span style="color: rgb(102, 102, 102);">- 경품 양도는 직계 존·비속 관계에 한하여 가능하며, 가족 관계를 증명할 수 있는 서류를 제출하셔야 합니다.</span></p><p><span style="color: rgb(102, 102, 102);">- 경품으로 제공하는 크루즈 상품은 2024년 5월 26일(일) - 31일(금) 5박 6일, 단 1회 출발로 상품 변경 및 날짜 조정은 불가능합니다</span></p><p><span style="color: rgb(102, 102, 102);">- 5만원이 초과하는 경품은 상품가의 22% 제세공과금이 발생하며 당첨자 본인 부담입니다. (1팀당 2인 상품가 5,180,000원)</span></p><p><span style="color: rgb(102, 102, 102);">- 제세공과금 처리에 필요한 신분증 사본 요청 거부 시 당첨이 취소됩니다.</span></p><p><span style="color: rgb(102, 102, 102);">- 본 상품은 특별약관 적용 상품으로 NO SHOW로 인한 환불은 불가능합니다.</span></p><p><br></p>', '2024-05-23 11:18:57'),
        (5, 'Cloud Trip 이용약관 변경사항', '<p><strong style="color: rgb(102, 102, 102);">안녕하세요. CLOUDTRIP㈜입니다.</strong></p><p><br></p><p><span style="color: rgb(102, 102, 102);">항상 Cloud Trip을&nbsp;이용해 주시고 관심 가져 주시는 고객님께 감사의 말씀 드립니다.</span></p><p><span style="color: rgb(102, 102, 102);">이용약관&nbsp;</span><strong style="color: rgb(0, 0, 128);">"제3조 (약관명시 및 개정)"</strong><span style="color: rgb(102, 102, 102);">에 의거하여 고객님께&nbsp;</span><strong style="color: rgb(0, 0, 128);">약관 변경 내용</strong><span style="color: rgb(102, 102, 102);">을 공지 드립니다.</span></p><p><br></p><p><br></p><p><strong style="color: rgb(102, 102, 102);">1. 개정대상: CLOUDTRIP 멤버스 약관</strong></p><p><br></p><p><strong style="color: rgb(102, 102, 102);">2. 개정사유: 구매 포인트 사용 및 환불에 대한 약관 변경</strong></p><p><br></p><p><strong style="color: rgb(102, 102, 102);">3. 적용일시: 2024. 05. 26</strong></p><p><br></p><p><strong style="color: rgb(102, 102, 102);">4. 개정내용</strong></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/7c38783b-edummy5.jpeg"></p>', '2024-05-23 11:33:46'),
        (6, '[공지] Cloud Trip 고객센터 임시 휴무 안내(6.23. 화)', '<p><span style="color: rgb(0, 0, 0);">안녕하세요.</span></p><p><br></p><p><span style="color: rgb(0, 0, 0);">Cloud Trip 고객센터 임시 휴무 안내드립니다.</span></p><p><br></p><ol><li data-list="bullet" class="ql-align-justify"><span class="ql-ui" contenteditable="false"></span><span style="color: rgb(0, 0, 0);">■휴무 일시: 2024년 4월 23일 화요일, 09:00 ~ 18:00 (종일)</span></li></ol><p class="ql-align-justify"><br></p><p><span style="color: rgb(0, 0, 0);">※ 내부 일정 관계로 방문, 전화상담(1670-0880), 실시간상담채팅이 제한됩니다.</span></p><p><br></p><p><span style="color: rgb(0, 0, 0);">4월 23일 외 기존 운영시간에는 변동 없으며, 예약서비스는 정상 예약 및 운영되오니 이용에 참고 부탁드립니다.</span></p><p><span style="color: rgb(0, 0, 0);">※ Cloud Trip 운영시간: 월~금, 09:00 ~ 18:00 (주말 및 공휴일 휴무)</span></p><p><br></p><p><span style="color: rgb(0, 0, 0);">긴급한 사항이 있을 시, 아래의 비상 연락처로 연락 주시기 바랍니다.</span></p><ol><li data-list="bullet" class="ql-align-justify"><span class="ql-ui" contenteditable="false"></span><span style="color: rgb(0, 0, 0);">■차량(버스/미니밴) 문의: ☎ 02-6674-1111</span></li><li data-list="bullet" class="ql-align-justify"><span class="ql-ui" contenteditable="false"></span><span style="color: rgb(0, 0, 0);">■보조기기 문의:&nbsp;☎ 070-8893-2222</span></li></ol><p><span style="color: rgb(0, 0, 0);">※ 위 연락처는 차량 및 보조기기 수행 운영사로 일부 문의에 답변이 어려울 수 있습니다.</span></p><p><br></p>', '2024-05-23 11:41:56'),
        (7, '2024 여행용 보조기기 대여서비스 예약 개시 안내(4/12)', '<p><span style="color: rgb(0, 0, 0);">2023년 4월 12일 금요일, 오전 9시</span></p><p><span style="color: rgb(0, 0, 0);">Cloud Trip 여행용 보조기기 대여서비스 예약을 개시합니다.</span></p><p><span style="color: rgb(0, 0, 0);">이용은 4월 19일(금)부터 하실 수 있습니다.</span></p><p><br></p><p><span style="color: rgb(0, 0, 0);">보조기기 대여 이용과 관련된 자세한 사항은</span></p><p><span style="color: rgb(0, 0, 0);">본 홈페이지 ‘예약&gt;보조기기&gt;여행용 보조기기 대여’를 참고하여 주시기 바랍니다.</span></p><p><span style="color: rgb(0, 0, 0);">바로기기 클릭:&nbsp;</span><u style="color: rgb(22, 63, 199);"><a href="https://www.seouldanurim.net/rent-asst" rel="noopener noreferrer" target="_blank">https://www.seouldanurim.net/rent-asst</a></u></p><p><br></p><p><strong style="color: rgb(0, 0, 255);">※ 차량 예약서비스 개시 일정은 미정으로 추후 공지드릴 예정입니다.</strong></p><p><br></p><p><span style="color: rgb(0, 0, 0);">문의 사항이 있으신 경우 Cloud Trip 고객센터(T.1111-0880)로</span></p><p><span style="color: rgb(0, 0, 0);">연락하여 주시기 바랍니다.</span></p><p><br></p><p><span style="color: rgb(0, 0, 0);">감사합니다.</span></p><p><br></p>', '2024-05-23 11:42:49'),
        (8, '5회차 월말여행 관통Tour 제출가이드', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/bb95fc20-0dummy7.png"></p>', '2024-05-23 11:44:32'),
        (9, '5월 SSAFY MeetUp 이벤트! 참여도 하고 선물도 받는 기회♥ (~5/20) - 1', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/073cd4cf-020240509112009485_WRFKR93Q.png"></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/4d625f0b-b20240509112314819_TG98JDH4.png"></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/130cc45a-f20240509112423382_2PTT2EE1.png"></p>', '2024-05-23 11:48:03'),
        (10, '5월 SSAFY MeetUp 이벤트! 참여도 하고 선물도 받는 기회♥ (~5/20) - 2', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/34049932-720240516170116740_OH5R2E2J.png"></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/394e9ba7-620240509112521590_AQWKOS3I.png"></p>', '2024-05-23 11:48:36'),
        (11, '[Q&A] Cloud Trip에서 제공하는 데이터를 사용하고 싶습니다.', '<p><span style="background-color: rgb(246, 246, 246); color: rgb(51, 51, 51);">A. Visit Seoul에서 외부에 제공 가능한 공공데이터는 ''서울열린데이터광장'' 홈페이지를 통해 제공하고 있습니다.</span></p>', '2024-05-23 11:49:38'),
        (12, '[Q&A] Cloud Trip Bus 탑승 장소 및 출발지는 어디인가요?', '<p><span style="color: rgb(51, 51, 51);">A. 서울시티투어버스는 코스별로 출발장소가 다릅니다.</span></p><p><span style="color: rgb(51, 51, 51);">각 코스별 출발장소는 https://korean.visitseoul.net/citytourbus 에서 확인하실 수 있습니다.</span></p><p><br></p>', '2024-05-23 11:55:02'),
        (13, '[Q&A] Cloud Trip 앱은 어디서 다운로드 받을 수 있나요?', '<p>A. Cloud Trip <span style="background-color: rgb(246, 246, 246); color: rgb(51, 51, 51);">앱은 구글 플레이스토어와 애플스토어에서 무료로 다운로드 받아 사용할 수 있습니다.&nbsp;</span></p>', '2024-05-23 11:55:59'),
        (14, '[Q&A] [공연] 공연 시간보다 늦게 도착해도 입장할 수 있나요?', '<p>A. <span style="background-color: rgb(246, 246, 246); color: rgb(51, 51, 51);">이는 공연마다 다릅니다. 일부 공연(특히 클래식 또는 오페라)은 시작 후 입장이 불가합니다. 이 때는 중간 휴식 시간에 들어가서 나머지 공연을 감상할 수 있습니다. 공연 시작 후 입장할 시에는 중간 휴식 시간에 기존 예약 좌석을 찾아가야 할 수도 있습니다. 편리하고 쾌적하게 공연을 즐길 수 있도록 시작 전에 여유 있게 도착해 주십시오.</span></p>', '2024-05-23 11:56:38'),
        (15, '[Q&A] 마이페이지에서 항공권 취소요청이 안되는데 어떻게 해아하나요?', '<p><strong style="background-color: rgb(249, 249, 249); color: rgb(81, 143, 187);">A.</strong><span style="background-color: rgb(249, 249, 249); color: rgb(17, 17, 17);">&nbsp;환불 접수/요청 가능시간은 월~금 09:00~17:00 (주말&amp;공휴일 제외) 입니다.</span></p><p><span style="background-color: rgb(249, 249, 249); color: rgb(17, 17, 17);">&nbsp;&nbsp;이후 시간에는 환불 요청이 불가하며, 출발 임박한 일정에 대해서는 항공사 고객센터 통하여 노쇼처리 되지 않게 예약</span></p><p><span style="background-color: rgb(249, 249, 249); color: rgb(17, 17, 17);">&nbsp;&nbsp;일정정리 먼저 하신 후 참좋은여행 환불 접수 가능 시간내에 환불 요청 하시기 바랍니다.</span></p>', '2024-05-23 12:00:23'),
        (16, '[Q&A] 환불절차는 어떻게 되나요?', '<p><strong style="color: rgb(81, 143, 187);">A.&nbsp;</strong><strong>Cloud Trip</strong><span style="color: rgb(94, 94, 94);"> 홈페이지를 통해 구매하신 항공권의 환불은 마이페이지 &gt; 예약상세 &gt; 환불요청 하시면 담당자 확인 후 처리 가능합니다.</span></p><p><span style="color: rgb(17, 17, 17);">&nbsp;&nbsp;구매하신 항공권 규정에 따라 환불수수료는 다르게 적용되므로 요금규정을 반드시 재확인 후 환불 요청하시기 바랍니다.&nbsp;</span></p><p><br></p><p><span style="color: rgb(94, 94, 94);">&nbsp;① 환불 접수 방법&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;- 마이페이지 &gt; 예약상세 &gt; 요금규정 확인 후 &gt; 환불 요청&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;② 환불 수수료 재안내&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;- 예약 시 남기신 휴대폰 번호로 정확한 환불 수수료 안내.</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;- 수수료 확인 후 환불 확정 및 철회 요청 온라인 상담에 남기기.</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;③ 담당자 환불 접수 진행</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;&nbsp;- 여행사 수수료 1인 3만원씩 기존 결제카드로 재결제 진행 후 환불 접수 (여행사수수료는 일시불 결제 진행)</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;&nbsp;- 환불 완료 후 안내 문자 전송.</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;환불 확정 후 환불철회 불가하며, 기존 항공권은 재사용이 불가합니다.&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;환불접수/요청 시간내에 요청건에 대해서만 당일 접수되며, 업무시간 이외에 취소 요청하신 경우, 영업일 기준 익일에 접수됩니다.</span></p><p><br></p><p><span style="color: rgb(94, 94, 94);">&nbsp;※ 환불 접수/요청 가능 시간 (예약, 발권 업무 시간과는 다를 수 있습니다.)</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;- 월요일 ~ 금요일 09:00 ~ 17:00 (주말&amp;공휴일 업무 제외)</span></p><p><br></p>', '2024-05-23 12:01:08'),
        (17, '[Q&A] 환불 후 환불기간은 얼마나 걸리나요?', '<p><strong style="color: rgb(81, 143, 187);">A.</strong><span style="color: rgb(94, 94, 94);">&nbsp;현금 환불은 항공사를 통해 항공권 환불금이 여행사로 환불 되면 최초 입금하셨던 고객님의 환불계좌로 송금처리되며,</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;환불 접수일로부터 약 1~2주 정도의 시간이 소요 됩니다.</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;신용카드 환불은 결제하신 카드사를 통해 환불됩니다. 환불 접수일로부터 1~2주 정도의 시간이 소요됩니다.</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;결제하신 카드사 결제일에 따라 환불 방법은 다를 수 있으니, 자세한 취소내역은 카드사 통하여 확인해주시기 바랍니다.</span></p><p><br></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;※ 환불 접수/요청 가능 시간 (예약, 발권 업무 시간과는 다를 수 있습니다.)</span></p><p><span style="color: rgb(94, 94, 94);">&nbsp;&nbsp;&nbsp;- 월요일 ~ 금요일 09:00 ~ 17:00 (주말&amp;공휴일 업무 제외)</span></p><p><br></p>', '2024-05-23 12:01:32'),
        (18, '9월 베스트 여행 후기 이벤트 당첨 발표', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/5bc2e80a-2dummy8.jpeg"></p><p><br></p><p><span style="color: rgb(102, 102, 102);">안녕하세요?&nbsp;참좋은여행입니다.</span></p><p><span style="color: rgb(102, 102, 102);">행복한 여행의 기억을 기록으로 남겨주신 모든 고객님들께 깊이 감사드립니다.</span></p><p><span style="color: rgb(102, 102, 102);">마음을 담아&nbsp;</span><strong style="color: rgb(102, 102, 102);">Cloud Trip&nbsp;50,000&nbsp;포인트</strong><span style="color: rgb(102, 102, 102);">를 적립하였습니다.&nbsp;</span></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/61c9e6cc-9%E1%84%89%E1%85%B3%E1%84%8F%E1%85%B3%E1%84%85%E1%85%B5%E1%86%AB%E1%84%89%E1%85%A3%E1%86%BA%202024-05-23%20%E1%84%8B%E1%85%A9%E1%84%92%E1%85%AE%209.06.13.png"></p><p><br></p><p><br></p><p><span style="color: rgb(102, 102, 102);">소중한 기억이 추억으로 간직되길 바라며</span></p><p><span style="color: rgb(102, 102, 102);">다음 여행도 참좋은여행에서 만나 뵙길 기대하겠습니다.</span></p><p><span style="color: rgb(102, 102, 102);">감사합니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><span style="color: rgb(102, 102, 102);">다시 함께,&nbsp;Cloud Trip</span></p><p><br></p>', '2024-05-23 12:06:26'),
        (19, '[괌] 괌 공항 미팅방법', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/06a427d4-0dummy9.png"></p>', '2024-05-23 12:07:17'),
        (20, '홈페이지 및 전체 서비스 점검 안내', '<p><span style="color: rgb(102, 102, 102);">홈페이지 방문 고객님께 안내 드립니다.</span></p><p><strong style="color: rgb(102, 102, 102);">시스템 점검</strong><span style="color: rgb(102, 102, 102);">으로 모든 서비스 이용이 일시적으로 중단되니 양해 부탁드립니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><span style="color: rgb(255, 0, 0);">※&nbsp;</span><strong style="color: rgb(255, 0, 0);">점검 일정 : 2023년 6월 28일 수요일 20시 ~ 21시</strong></p><p><span style="color: rgb(128, 128, 128);">※&nbsp;상기 작업 시간은 사정에 의해 변경될 수 있습니다.</span></p><p><span style="color: rgb(128, 128, 128);">※&nbsp;예정된 시간에 작업이 완료될 수 있도록 노력하겠습니다.</span></p><p><span style="color: rgb(102, 102, 102);">&nbsp;</span></p><p><span style="color: rgb(102, 102, 102);">서비스 이용에 불편을 드려 죄송합니다.</span></p><p><span style="color: rgb(102, 102, 102);">보다 나은 서비스를 위해 최선을 다하겠습니다.</span></p><p><span style="color: rgb(102, 102, 102);">감사합니다.</span></p><p><br></p>', '2024-05-23 12:07:33'),
        (21, '5/13 출발 터키항공 비즈니스 상품 안내문', '<p><span style="color: rgb(102, 102, 102);">안녕하세요.</span></p><p><span style="color: rgb(102, 102, 102);">Cloud Trip 비즈니스 터키 일주 10일 담당자입니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">여행 안내문입니다.</span></p><p><strong style="color: rgb(0, 128, 0);">동행자와 반드시 내용 공유 바랍니다.</strong></p><p><br></p><p><span style="color: rgb(102, 102, 102);">금일 발송한 안내사항은 여행 전반적인 내용을 포함하고 있습니다.</span></p><p><br></p><p><span style="color: rgb(0, 128, 0);">개별적인 전화/카톡/문자는 제공되지 않으며 새로운 사항이 추가될 경우 업데이트 후 연락드리겠습니다.</span></p><p><br></p><p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/b48bb710-5%E1%84%91%E1%85%A1%E1%84%86%E1%85%AE%E1%86%A8.jpeg"></p><p><br></p><p><span style="color: rgb(102, 102, 102);">기재된 내용 이외에 더 궁금하신 부분이 있다면 유선상으로 문의 부탁드립니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">☎ 프리미엄팀 02-2185-1234</span></p><p><br></p><p><br></p><p><br></p><p><span style="color: rgb(102, 102, 102);">■ 공항 미팅 안내</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">- 일자 : 5월 13일 토요일&nbsp;20:00</span></p><p><span style="color: rgb(102, 102, 102);">- 장소 : 인천국제공항 1터미널 3층 출국장 14번 출입구 앞 N카운터 참좋은여행 테이블</span></p><p><span style="color: rgb(102, 102, 102);">- 담당 : 공항서비스팀 1644-0767 / 현지 가이드 김홍진 +90-536-912-3654</span></p><p><span style="color: rgb(102, 102, 102);">- 항공 : 터키항공 TK091</span></p><p><span style="color: rgb(102, 102, 102);">- 항공출발시간 : 5/13 23:35 (약 11시간 35분 소요)</span></p><p><span style="color: rgb(102, 102, 102);">- 항공편 관련 안내 : 출발 당일 Cloud Trip 공항직원과 미팅 후에 카운터로 이동하여 개별적으로 체크인이 진행 됩니다.</span></p><p><span style="color: rgb(255, 0, 0);">※ 이스탄불 공항에서 짐 찾고 나오시면 오른쪽에 버거킹이 있습니다.</span></p><p><span style="color: rgb(255, 0, 0);">버거킹 앞에서 가이드를 만나시게 됩니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">※터키항공 비즈니스클래스의 1인당 제공 위탁수하물은 최대 40kg(가로×세로×높이 158cm) 1개</span></p><p><span style="color: rgb(102, 102, 102);">기내수하물은 최대 8kg(55*40*23cm) 2개 입니다.</span></p><p><span style="color: rgb(102, 102, 102);">※터키 국내선도 동일하게 40kg 적용됩니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">※100ml이상 액체류는 수하물짐으로, 보조배터리는 기내로 들고 타셔야 합니다.</span></p><p><span style="color: rgb(102, 102, 102);">기내로 액체류를 들고 타실때는 용기용량이 100ml 미만으로 최대 1리터까지 가능하시며</span></p><p><span style="color: rgb(102, 102, 102);">지퍼백에 넣어서 준비하셔야 가능합니다. 라이터,성냥은 1개까지 반입가능합니다.</span></p><p><br></p><p><strong style="color: rgb(0, 128, 0);">※간혹 수하물이 분실 또는 지연되는 경우가 있습니다.</strong></p><p><strong style="color: rgb(0, 128, 0);">부치는 짐에는 현금, 귀중품, 상비약, 고가의 전자제품 등을 절대 넣지 마세요.</strong></p><p><br></p><p><strong style="color: rgb(0, 128, 0);">현금, 여권, 입국 서류, 회사 관련 중요서류, 의약품 등의 귀중품 및 긴급 사용 물품 등은 반드시 기내 휴대를 당부드리며, 위탁수하물에 반드시 NAME TAG을 달아주시기 바랍니다.</strong></p><p><br></p><p><span style="color: rgb(102, 102, 102);">※과일, 육류, 축산품 반입은 절대 불가합니다.</span></p>', '2024-05-23 12:08:38'),
        (22, '	5/13 출발 터키항공 비즈니스 상품 안내문 (환전 & 준비물)', '<p><span style="color: rgb(102, 102, 102);">■ 환전 안내</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">: 유로화 또는 달러</span></p><p><span style="color: rgb(102, 102, 102);">선택관광 비용의 경우 유로화가 원칙입니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">1. 개인 경비 : 하루 약 40~90유로 평균 약 300유로 또는 달러 (쇼핑 비용 별도 준비)</span></p><p><span style="color: rgb(102, 102, 102);">- 신용카드 사용 가능합니다. 해외 사용 가능한 본인 명의 VISA나 MASTER 카드를 준비하시면 됩니다.</span></p><p><span style="color: rgb(102, 102, 102);">카드 비밀번호 확인 후 지참하세요.</span></p><p><span style="color: rgb(102, 102, 102);">2. 선택관광 비용 : 총 1,120유로</span></p><p><span style="color: rgb(102, 102, 102);">- 이스탄불 프리미엄 야경투어 80유로</span></p><p><span style="color: rgb(102, 102, 102);">- 카파도키아 열기구 350유로</span></p><p><span style="color: rgb(102, 102, 102);">- 지프 사파리 90유로</span></p><p><span style="color: rgb(102, 102, 102);">- 안탈랴 유람선 60유로</span></p><p><span style="color: rgb(102, 102, 102);">- 올림포스 케이블카 90유로</span></p><p><span style="color: rgb(102, 102, 102);">- 터키 고고학 시리즈 투어 180유로</span></p><p><span style="color: rgb(102, 102, 102);">- 파묵칼레 카트투어 60유로</span></p><p><span style="color: rgb(102, 102, 102);">- 파묵칼레 열기구 210유로</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">5. 기본 매너 팁은 달러로 준비하셔도 됩니다. (1박 1달러)</span></p><p><span style="color: rgb(102, 102, 102);">※ 매너팁은 자율적 선택입니다.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">■ 현지날씨/복장</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">터키 여행기간 중 온도는 최저 8°~최고 24℃ 정도입니다.</span></p><p><strong style="color: rgb(0, 128, 0);">지역마다 기온이 다르며 긴팔, 겉옷을 골고루 준비해주세요.</strong></p><p><span style="color: rgb(102, 102, 102);">기본적으로 현재 터키는 한국 날씨보다 따뜻합니다.</span></p><p><span style="color: rgb(102, 102, 102);">비 소식이 있을 수 있습니다 우산도 준비해주세요.</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">■ 준비물</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">① 여권 (출국 전 서명란 기입 필수)</span></p><p><span style="color: rgb(102, 102, 102);">- 유효기간이 6개월 이상 남아있어야 하며 여권서명란에 반드시 본인의 서명이 들어가 있어야 합니다.</span></p><p><span style="color: rgb(102, 102, 102);">여권 사증은 5면 이상 남아 있어야 하며 훼손된 여권의 경우 입국이 불가할 수도 있으니 한번 더 확인 바랍니다.</span></p><p><span style="color: rgb(102, 102, 102);">② 세면도구 - 칫솔, 치약, 면도기, 샴푸, 린스. 여성용품 등 (호텔내에 비치되어 있지 않으며 물비누만 있습니다)</span></p><p><span style="color: rgb(102, 102, 102);">③ 헤어 드라이기, 개인커피포트 (별도준비)</span></p><p><span style="color: rgb(102, 102, 102);">- 전기포트, 슬리퍼가 없을수도 있습니다.</span></p><p><span style="color: rgb(102, 102, 102);">헤어드라이기는 대부분 있으나 바람이 약하거나 잘 안되는 경우가 있습니다.</span></p><p><span style="color: rgb(102, 102, 102);">④ 상비약 및 방역 관련 제품</span></p><p><span style="color: rgb(102, 102, 102);">감기약/멀미약/소화제/지사제/후시딘/피로회복제/혈압약 등 개인 의약품과 KF94 마스크, 손소독제 등</span></p><p><span style="color: rgb(102, 102, 102);">⑤ 카메라, 멀티탭, 충전기, 수신기용 이어폰, (필요 시) 터키 유심(USIM) 등</span></p><p><span style="color: rgb(102, 102, 102);">⑥ 우산, 우의, 썬크림, 선글라스, 모자, 실내용 슬리퍼 등</span></p><p><span style="color: rgb(102, 102, 102);">⑦ 첫날 호텔 도착 시 드실 물 (500ml)과 간식</span></p><p><span style="color: rgb(102, 102, 102);">⑧(필요 시) 컵라면, 김, 마른반찬, 고추장, 통조림 반찬 등 냄새 안 나는 음식</span></p><p><span style="color: rgb(102, 102, 102);">⑨ 의류, 잠옷, 보조가방, 운동화 등</span></p><p><span style="color: rgb(102, 102, 102);">⑩&nbsp;</span><strong style="color: rgb(0, 128, 0);">파묵칼레 온천욕 복장 수영복, 수영모 등 (래쉬가드 불가)</strong></p><p><br></p><p><br></p><p><span style="color: rgb(102, 102, 102);">■ 식사</span></p><p><br></p><p><span style="color: rgb(102, 102, 102);">- 식사는 현지식이 대부분이며 입에 맞지 않을 수 있습니다.</span></p><p><strong style="color: rgb(0, 128, 0);">현지식은 주로 닭고기가 들어간 케밥</strong><span style="color: rgb(102, 102, 102);">&nbsp;(쉬쉬/아다나/항아리/믹스/도네르 등)이 제공됩니다</span></p><p><span style="color: rgb(102, 102, 102);">필요시 컵라면/김/고추장등 냄새가 안나는 것으로 준비하시면 식사 하시는데 도움이 됩니다.</span></p><p><span style="color: rgb(102, 102, 102);">김치 등 향이 강한 음식은 레스토랑에서는 취식이 불가하며 소주를 포함한 외부 주류 반입 또한 금지입니다.</span></p>', '2024-05-23 12:09:14'),
        (23, '건전한 Cloud Trip을 위한 마음가짐 안내', '<p><img src="https://trip-media-server.s3.ap-northeast-2.amazonaws.com/e112f739-620240319101250750_04QZ4UZ4.png"></p>', '2024-05-23 12:24:07');