SELECT c.comment_id, c.user_id, u.nickname, c.content, c.created_at, u.profile_image
FROM review_comment c JOIN users u ON c.user_id = u.user_id
WHERE review_id = #{reviewId}

SELECT c.comment_id, c.user_id, u.nickname, c.content, c.created_at, u.profile_image
FROM review_comment c JOIN users u ON c.user_id = u.user_id
WHERE c.user_id = #{userId}
